import { getExpenses, addExpense, deleteExpense } from './services/expenseService';

const fetchExpenses = async () => {
  const token = localStorage.getItem('token');
  const res = await getExpenses(token);
  setExpenses(res.data);
};

const handleAddExpense = async () => {
  if (expenseData.amount && expenseData.description && expenseData.category) {
    const token = localStorage.getItem('token');
    await addExpense(expenseData, token);
    fetchExpenses(); // Reload danh sách
  }
};

const handleDeleteExpense = async (id) => {
  const token = localStorage.getItem('token');
  await deleteExpense(id, token);
  fetchExpenses();
};
